// Obtém todos os itens de entrada
const items = $input.all();

// Array para armazenar somente os filtrados
const filtrados = [];

// Loop pelos itens recebidos
for (const item of items) {
    const ticket = item.json;

    // Verifica se existe "clients" e se é array
    if (Array.isArray(ticket.clients)) {

        // Procura algum cliente com businessName = "Indigo Ti"
        const hasIndigo = ticket.clients.some(c => c.businessName === "Indigo Ti");

        if (hasIndigo) {
            filtrados.push({
                json: ticket
            });
        }
    }
}

// Retorna apenas os registros filtrados
return filtrados;

